package week1.day1;

public class OddOrEven {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Print if the given number is odd or even
		
				int n = 99;
				
				if(n%2==0)
				{
					System.out.println("Even Number");
				}
				else
				{
					System.out.println("Odd Number");
					
				}
						

	}
}


	


